import React from 'react';

import './Input.scss';

const input = props => {
    let inputElement = null;

    switch (props.elementType){
        case ('input'):
            inputElement = <input 
                className="field_input"
                {...prop.elementConfig}
                value = {props.value} />
            break;

        case ('select'): 
            inputElement = <select 
                className="field_input"
                value = {props.value}
                >
                    {props.options.map(option => (
                        <option key={option.value} value={option.value}>{option.displayValue}</option>
                    ))}
                </select>
            break;

        default:
            inputElement = <input 
                className="field_input"
                {...prop.elementConfig}
                value = {props.value} />
    }
    return(
        <form>
            {inputElement}
        </form>
    )
}

export default input;
