import React, { Component } from 'react';
import {Route,Switch} from 'react-router-dom';

import Nav from './components/Nav/Nav';
import HorizontalNav from './components/Nav/HorizontalNav/HorizontalNav';
import Footer from './components/Footer/Footer';
import AboutUs from './containers/AboutUs/AboutUs';
import BackToTop from './components/BackToTop/BackToTop';
import './App.css';

class App extends Component {

  backToTopHandler = (scrollDuration) => {
    // const scrollStep = -window.scrollY / (scrollDuration / 15),
    //     scrollInterval = setInterval(function(){
    //     if ( window.scrollY !== 0 ) {
    //         window.scrollBy( 0, scrollStep );
    //     }
    //     else clearInterval(scrollInterval); 
    // },15);
    let cosParameter = window.scrollY / 2,
        scrollCount = 0,
        oldTimestamp = performance.now();
    function step (newTimestamp) {
        scrollCount += Math.PI / (scrollDuration / (newTimestamp - oldTimestamp));
        if (scrollCount >= Math.PI) window.scrollTo(0, 0);
        if (window.scrollY === 0) return;
        window.scrollTo(0, Math.round(cosParameter + cosParameter * Math.cos(scrollCount)));
        oldTimestamp = newTimestamp;
        window.requestAnimationFrame(step);
    }
    window.requestAnimationFrame(step);
  }

  render(){
    return (
      <div className="App">
        <Nav />
        <HorizontalNav />
        <Switch>
          <Route path="/" exact component={AboutUs}/>
          <Route path="/course" render={() => <h1>Course</h1>}/>
          <Route path="/blog" render={() => <h1>Blog</h1>}/>
          <Route path="/student" render={() => <h1>Student</h1>}/>
          <Route path="/document" render={() => <h1>Document</h1>}/>
          <Route path="/contact" render={() => <h1>Contact</h1>}/>
        </Switch>
        <Footer />
        <BackToTop clicked={() => this.backToTopHandler(600)}/>
      </div>
    );
  }
}

export default App;
