import React, { Component } from 'react';
import {Route,Switch} from 'react-router-dom';

import Nav from './components/Nav/Nav';
import HorizontalNav from './components/Nav/HorizontalNav/HorizontalNav';
import Footer from './components/Footer/Footer';
import AboutUs from './containers/AboutUs/AboutUs';
import BackToTop from './components/BackToTop/BackToTop';
import './App.css';

class App extends Component {

  backToTopHandler = (e) => {
    e.preventDefault();
    window.scroll = () => {
      window.scrollY = 0;
    }

  }

  render(){
    return (
      <div className="App">
        <Nav />
        <HorizontalNav />
        <Switch>
          <Route path="/" exact component={AboutUs}/>
          <Route path="/course" render={() => <h1>Course</h1>}/>
          <Route path="/blog" render={() => <h1>Blog</h1>}/>
          <Route path="/student" render={() => <h1>Student</h1>}/>
          <Route path="/document" render={() => <h1>Document</h1>}/>
          <Route path="/contact" render={() => <h1>Contact</h1>}/>
        </Switch>
        <Footer />
        <BackToTop clicked={this.backToTopHandler}/>
      </div>
    );
  }
}

export default App;
