import React, { Component } from 'react';

import Input from '../../components/UI/Input/Input';

import './AboutUs.scss';

class AboutUs extends Component {
    state = {
        form: {
            course:{
                elementType: 'select',
                options: [
                    {value: '',displayValue: 'Chọn khoá học'},
                    {value: 'Digital Performance Marketing',displayValue: 'Digital Performance Marketing'},
                    {value: 'Intermediate Level',displayValue: 'Intermediate Level'},
                    {value: 'Advance Level',displayValue: 'Advance Level'},
                ],
                value: ''
            },
            fullname: {
                elementType: 'input',
                elementConfig: {
                    type:'text',
                    placeholder: 'Họ và tên',
                    name: 'fullname'    
                },
                value: ''
            },
            email:{
                elementType: 'input',
                elementConfig: {
                    type:'text',
                    placeholder: 'Email',
                    name: 'email'    
                },
                value: ''
            },
            phone:{
                elementType: 'input',
                elementConfig: {
                    type:'text',
                    placeholder: 'Số điện thoại',
                    name: 'phone'    
                },
                value: ''
            },
        },
        val: ""
    }

    inputChangedHandler = (event) => {
        // const updatedForm = {...this.state.form}
        // const updatedFormElement = {...updatedForm[inputIdentifier]}
        // updatedFormElement.value = event.target.value;
        // updatedForm[inputIdentifier] = updatedFormElement;
        // this.setState({form: updatedForm})
        this.setState({val: event.target.value})
    }

    render(){
        // const formArrayElement = []
        // for (let key in this.state.form){
        //     formArrayElement.push({
        //         id: key,
        //         config: this.state.form[key]
        //     })
        // }
        // let registerForm = formArrayElement.map(formEle => (
        //     <Input 
        //         key={formEle.id}
        //         elementType= {formEle.config.elementType}
        //         elementConfig= {formEle.config.elementConfig}
        //         options = {formEle.config.options}
        //     />
        // ))

        let registerForm = (
            <form>
                <Input elementType={this.state.form.course.elementType} 
                        value={this.state.form.course.value} 
                        options={this.state.form.course.options}  
                        changed={(event) =>this.inputChangedHandler(event,0)}
                />
                <Input  elementType={this.state.form.fullname.elementType} 
                        elementConfig = {this.state.form.fullname.elementConfig}
                        value={this.state.val}  
                        changed={(event) =>this.inputChangedHandler(event,1)}
                />
                <div class="row">
                  <div class="col-md-6">
                    <Input  elementType={this.state.form.email.elementType} 
                            elementConfig = {this.state.form.email.elementConfig}
                            value={this.state.form.email.value}  
                            changed={(event) =>this.inputChangedHandler(event,2)}
                    />
                  </div>
                  <div class="col-md-6">
                    <Input  elementType={this.state.form.phone.elementType} 
                            elementConfig = {this.state.form.phone.elementConfig}
                            value={this.state.form.phone.value}  
                            changed={(event) =>this.inputChangedHandler(event,3)}
                    />
                  </div>
                </div>
            </form>
        );


        return(
            <div>
                <header id="header">
                    <div class="container">
                        <div class="row">
                            <div class="col-md-6">
                                <p class="title">
                                    Lorem ipsum
                                </p>
                                <p>
                                    Lorem ipsum dolor sit amet, consectetuer adipiscing elit, sed diam nonummy nibh euismod tincidunt ut laoreet dolore magna aliquam erat volutpat. Ut wisi enim ad minim veniam, quis nostrud exerci tation ullamcorper suscipit lobortis nisl ut aliquip ex ea commodo consequat. 
                                </p>
                                <p>
                                    Lorem ipsum dolor sit amet, cons ectetuer adipiscing elit, sedv diam nonummy nibh euismod tincidunt ut laoreet dolore magna aliquam erat volutpat. Ut wisi enim ad minim veniam, quis nostrud exerci tation ullamcorper suscipit lobortis nisl ut aliquip ex ea commodo consequat.
                                </p>
                                <div class="header_form">
                                    <div class="d-flex align-items-center">
                                        <p>
                                            Nhận tư vấn khóa học miễn phí
                                        </p>
                                        <figure class="mr-0 ml-auto">
                                            <img src="images/may_bay_giay.png" alt="" class="img-fluid mx-auto d-block" />
                                        </figure>
                                    </div>
                                    <form>
                                        {registerForm}
                                    </form>

                                    <p>
                                        {this.state.form.fullname.value}    
                                    </p>
                                </div>
                            </div>
                            <div class="col-md-6 my-auto">
                                <figure>
                                    <img src="images/header_hinh.png" alt="" class="img-fluid mx-auto d-block" />
                                </figure>
                            </div>
                        </div>
                    </div>
                </header>
            </div>
        )
    }
}

export default AboutUs;
